
public class arraymethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a[] = {1,2,3,4,5};
		System.out.println(a[0]);
		System.out.println(a.length);
		int c=10;
		int b[] = new int[c];
		System.out.println(b);
		for(int i : b) {
			System.out.println(i);
		}
		
		
		
	}

}
